 MKPT_Item = {}\nMKPT_Item.__index = MKPT_Item\n\nfunction MKPT_Item:New(profession, itemInfo, index)\n    local item = {}\n    setmetatable(item, MKPT_Item)\n    item.id = itemInfo.item\n    item.type = itemInfo.type\n    item.questIds = itemInfo.questId\n    item.waypoint = itemInfo.waypoint\n    item.kp = itemInfo.kp\n    item.unique = itemInfo.unique\n    item.profession = profession\n    item:RefreshItemInfo()\n    item.text = itemInfo.text\n    item.renown = itemInfo.renown\n    item.spellId = itemInfo.spell\n    item.index = index\n    return item\nend\n\nfunction MKPT_Item:RefreshItemInfo()    \n    local itemName, _, _, _, _, _, _, _, _, itemTexture, _ = C_Item.GetItemInfo(self.id)\n    if itemName then\n        self.name = itemName\n        self.icon = itemTexture\n    end\nend\n\n\nfunction MKPT_Item:GetRemainingKnowledgePoints()\n    local questCount = #self.questIds\n    \n    for _, questId in ipairs(self.questIds) do\n        if C_QuestLog.IsQuestFlaggedCompleted(questId) then\n            questCount = questCount - 1\n        end\n    end\n    \n    if self.unique then\n        return (questCount == #self.questIds and self.kp) or 0\n    else\n        return questCount * self.kp\n    end\nend\n\nfunction MKPT_Item:MeetRenownRequirements()\n    if self.renown then\n        local renownLevel = C_MajorFactions.GetCurrentRenownLevel(self.renown.majorFactionId) or 0\n        return renownLevel >= self.renown.levelRequired\n    end\n    return true\nend\n\nfunction MKPT_Item:Track()\n    WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", self:GetDescription())\n    local wp = self.waypoint\n    if not wp then return end\n    local mapPoint = UiMapPoint.CreateFromCoordinates(wp.map, wp.x, wp.y)\n    C_Map.SetUserWaypoint(mapPoint)\n    C_SuperTrack.SetSuperTrackedUserWaypoint(true)\n    local _, isTomTomLoaded = C_AddOns.IsAddOnLoaded(\"TomTom\")\n    if isTomTomLoaded and TomTom then\n        local itemName = self.name or \"Item not cached\"\n        local uid = TomTom:AddWaypoint(wp.map, wp.x, wp.y, {title=itemName, persistent=false, source=\"MKT_WA\"})\n        self.tomtomUid = uid\n    end\n    \nend\n\nfunction MKPT_Item:Untrack()\n    C_SuperTrack.SetSuperTrackedUserWaypoint(false)\n    C_Map.ClearUserWaypoint()\n    if TomTom and self.tomtomUid then\n        TomTom:RemoveWaypoint(self.tomtomUid)\n    end\n    WeakAuras.ScanEvents(\"MKPT_UPDATE_DESCRIPTION\", nil)\nend\n\nfunction MKPT_Item:IsHighlighted()\n    return aura_env.highlightedItemId == self.id\nend\n\nfunction MKPT_Item:ToggleHighlighted()\n    if self:IsHighlighted() then\n        aura_env.highlightedItemId = nil\n    else\n        aura_env.highlightedItemId = self.id\n    end\nend\n\nfunction MKPT_Item:GetDescription()\n    if not self.waypoint then\n        return self.text or \"Found on treasures around Khaz Algar\"\n    end\n    local renownText = \"\"\n    if not self:MeetRenownRequirements() then \n        local renownInfo = C_MajorFactions.GetMajorFactionData(self.renown.majorFactionId)\n        if renownInfo then\n            renownText = renownInfo.name..\": \\124cffFF0000\"..tostring(renownInfo.renownLevel)..\"\\124r/\"..tostring(self.renown.levelRequired)..\"\\n\"\n        end\n    end\n    if not self.name then\n        self:RefreshItemInfo()\n    end\n    \n    local itemDescription = self.text or self.name or \"Not loaded\"\n    \n    local mapInfo = C_Map.GetMapInfo(self.waypoint.map)\n    \n    return string.format(\"%s%s\\n%s - x:%.2f y:%.2f\", renownText, itemDescription, mapInfo.name, self.waypoint.x * 100, self.waypoint.y * 100)\nend\n\nfunction MKPT_Item:FillState(state)\n    state = state or {}\n    local remainingKnowledgePoints = self:GetRemainingKnowledgePoints()\n    state.changed = true\n    state.show = self.profession.expanded and remainingKnowledgePoints > 0 and (self.type == 2 or not aura_env.config.weekly)\n    state.name = self.name\n    state.icon = self.icon\n    state.itemId = self.id\n    state.glow = self:IsHighlighted()\n    state.rightText = \"+\"..tostring(remainingKnowledgePoints)\n    state.meetRequirements = self:MeetRenownRequirements()\n    state.type = self.type\n    state.index = self.index\n    return state\nend\n\nlocal MKPT_Profession = {}\nMKPT_Profession.__index = MKPT_Profession\n\nfunction MKPT_Profession:New(professionId, name, icon, skillLevel, maxSkillLevel, skillLine, index)\n    local profession = {}\n    setmetatable(profession, MKPT_Profession)\n    profession.id = professionId\n    profession.expanded = not aura_env.config.minimized\n    profession.name = name\n    profession.icon=icon\n    profession.skillLevel = skillLevel\n    profession.maxSkillLevel = maxSkillLevel\n    profession.skillLine = skillLine\n    profession.availableItems = {}\n    profession.index = index\n    for itemIndex, itemInfo in ipairs(db[professionId]) do\n        local item = MKPT_Item:New(profession, itemInfo, profession.index + itemIndex)\n        table.insert(profession.availableItems, item)\n    end\n    return profession\nend\n\nfunction MKPT_Profession:CalculateRemainingKps()\n    local weekly = 0\n    local unique = 0\n    for _, item in pairs(self.availableItems) do\n        local remaining = item:GetRemainingKnowledgePoints()\n        if item.type == 2 then\n            weekly = weekly + remaining\n        else\n            unique = unique + remaining\n        end\n    end\n    return { weekly=weekly, unique=unique }\nend\n\nfunction MKPT_Profession:GetAvailableItems()\n    return self.availableItems\nend\n\nfunction MKPT_Profession:FillState(state)\n    state = state or {}\n    \n    local remaining = self:CalculateRemainingKps()\n    state.changed = true\n    state.show = true\n    state.name = self.name..\" \"..tostring(self.skillLevel)..\"/\"..tostring(self.maxSkillLevel)\n    state.icon = self.icon\n    state.index = self.index\n    state.glow = false\n    state.type = 0\n    state.rightText = \"U: \"..tostring(remaining.unique)\n    state.leftText = \"W: \"..tostring(remaining.weekly)\n    \n    return state\nend\n\nfunction MKPT_Profession:RefreshSkillLevel()\n    local prof1, prof2, _ = GetProfessions()\n    if prof1 then      \n        local _,_,skillLevel,maxSkillLevel,_,_,skillLine, _ = GetProfessionInfo(prof1)\n        if self.skillLine == skillLine then\n            self.skillLevel = skillLevel\n            self.maxSkillLevel = maxSkillLevel\n        end\n    end\n    if prof2 then      \n        local _,_,skillLevel,maxSkillLevel,_,_,skillLine, _ = GetProfessionInfo(prof2)\n        if self.skillLine == skillLine then\n            self.skillLevel = skillLevel\n            self.maxSkillLevel = maxSkillLevel\n        end  \n    end\nend\n\nfunction MKPT_Profession:IsLearned()\n    local prof1, prof2, _ = GetProfessions()\n    local currentProfessions = {}\n    if prof1 then      \n        local _,_,_,_,_,_,skillLine, _ = GetProfessionInfo(prof1)\n        currentProfessions[skillLine] = true\n    end\n    if prof2 then      \n        local _,_,_,_,_,_,skillLine, _ = GetProfessionInfo(prof2)\n        currentProfessions[skillLine] = true    \n    end\n    return currentProfessions[self.skillLine]\nend\n\naura_env.FindItemByItemId = function (itemId)\n    if not itemId then\n        return nil\n    end\n    for _, profession in pairs(aura_env.GetProfessions()) do\n        for _, item in pairs(profession:GetAvailableItems()) do\n            if item.id == itemId then\n                return item\n            end\n        end\n    end\n    return nil\nend\n\naura_env.FindItemBySpellId = function (spellId)\n    if not spellId then\n        return nil\n    end\n    for _, profession in pairs(aura_env.GetProfessions()) do\n        for _, item in pairs(profession:GetAvailableItems()) do\n            if item.spellId == spellId then\n                return item\n            end\n        end\n    end\n    return nil\nend\n\naura_env.FindProfessionById = function (professionId)\n    return aura_env.GetProfessions()[professionId]\nend\n\naura_env.FindKhazAlgarProfessionSkillLineBySpellId = function (spellId)\n    return khazAlgarMainProfessionSpellIds[spellId]\nend\n\naura_env.GetProfessions = function ()\n    if aura_env.config.showAllProfessions then\n        return aura_env.GetAllProfessions()\n    else \n        return aura_env.GetLearnedProfessions()     \n    end\nend\n\naura_env.GetLearnedProfessions = function ()\n    if aura_env.professions then \n        return aura_env.professions \n    end\n    local professions = {}\n    local prof1, prof2 ,_ = GetProfessions()\n    \n    for _, professionSlot in ipairs{prof1, prof2} do\n        local name,icon,skillLevel,maxSkillLevel,_,_,skillLine, _ = GetProfessionInfo(professionSlot)\n        local khazAlgarSkillLine = skillLineIds[skillLine]\n        professions[khazAlgarSkillLine]= MKPT_Profession:New(khazAlgarSkillLine, name, icon, skillLevel, maxSkillLevel, skillLine, professionSlot * 1000)\n    end\n    \n    aura_env.professions = professions\n    \n    return professions\nend\n\naura_env.GetAllProfessions = function ()\n    if aura_env.professions then \n        return aura_env.professions \n    end\n    local professions = {}\n    local index = 0\n    for khazAlgarSkillLine, _ in pairs(db) do\n        index = index + 1\n        local info = C_TradeSkillUI.GetProfessionInfoBySkillLineID(khazAlgarSkillLine)\n        local icon = C_TradeSkillUI.GetTradeSkillTexture(khazAlgarSkillLine)\n        professions[khazAlgarSkillLine]= MKPT_Profession:New(khazAlgarSkillLine, info.professionName, icon, info.skillLevel, info.maxSkillLevel, info.parentProfessionID, index * 1000)\n    end\n    \n    aura_env.professions = professions\n    \n    return professions\nend\n\naura_env.RemoveMissingProfession = function ()\n    local professions = aura_env.GetProfessions()\n    for _, profession in pairs(professions) do\n        if not profession:IsLearned() then\n            professions[profession.id] = nil\n            return profession\n        end       \n    end\nend",
