},
["custom"] = "function(s, event, ...)\n    \n    if not s[\"\"] then\n        s[\"\"] = {\n            show = true,\n            changed = false,\n            \n            \n            stacks = 0, -- shooting stars count\n            firstRun = true, -- we need to track our first run, as opening the UI/reload will reset stacks and may be invalid\n            text = aura_env.firstRunTextPrefix,\n            cast = false, -- was full moon hardcast\n            energized = 0 -- full moon energized count\n        }\n        \n        return true\n    end\n    \n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        \n        local _,subevent,_,sourceGUID,_,_,_,_,_,_,_,spellID = ...\n        \n        if sourceGUID == UnitGUID(\"player\") then      \n            \n            if subevent == \"SPELL_PERIODIC_ENERGIZE\" and spellID == aura_env.spellIds.shootingStars then  \n                s[\"\"].stacks = s[\"\"].stacks + 1\n                s[\"\"].text = s[\"\"].stacks\n                s[\"\"].changed = true                \n                \n            elseif subevent == \"SPELL_ENERGIZE\" and spellID == aura_env.spellIds.fullMoon then         \n                s[\"\"].energized = s[\"\"].energized + 1\n                s[\"\"].changed = true\n                \n                -- send delayed event since this event fires for both proc and cast\n                C_Timer.After(aura_env.UAPB_OB_DELAY, function()\n                        WeakAuras.ScanEvents(\"UAPB_OB_DELAY\", true)\n                end)\n                \n            elseif subevent == \"SPELL_CAST_SUCCESS\" and spellID == aura_env.spellIds.fullMoon then\n                s[\"\"].cast = true -- full moon was hardcast\n                s[\"\"].changed = true\n            end\n            \n            return true \n        end\n        \n    elseif event == \"UAPB_OB_DELAY\" and ... then\n        \n        if (not s[\"\"].cast and s[\"\"].energized == 1) -- proc\n        or (s[\"\"].cast and s[\"\"].energized == 2) -- proc, then full moon cast within delay time\n        then     \n            s[\"\"].stacks = 0            \n            s[\"\"].firstRun = false\n            s[\"\"].text = s[\"\"].stacks\n            \n        else\n            s[\"\"].cast = false -- this was a hard cast full moon, reset the value\n        end\n        \n        s[\"\"].energized = s[\"\"].energized -1\n        s[\"\"].changed = true\n        \n        return true\n    end\n    \nend",
